import React, {} from 'react' 
import "../../App.css";
import { Table,Button,Container, ModalBody, FormGroup, ModalFooter, } from "reactstrap";
  
  
const data = [
        // elimine los valores
      ];


class CrearProyecto extends React.Component {
 
    state = {
        data: data,
        modalActualizar: false,
        modalInsertar: false,
        form: {
          id: "",
          Nombre: ""
        },
      };
      
      activarFormulario = () => {
        this.setFormulario ({Formulario :false});
      }

      mostrarModalActualizar = (dato) => {
        this.setState({
          form: dato,
          modalActualizar: true,
        });
      };
    
      cerrarModalActualizar = () => {
        this.setState({ modalActualizar: false });
      };
    
      mostrarModalInsertar = () => {
        this.setState({
          modalInsertar: true,
        });
      };
    
     
      insertar= ()=>{
        var valorNuevo= {...this.state.form};
        valorNuevo.id=this.state.data.length+1;
        var lista= this.state.data;
        lista.push(valorNuevo);
        this.setState({ modalInsertar: false, data: lista });
      }
    
      handleChange = (e) => {
        this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };
    
      render() {
       
      
        return (
      
            <div className="d-grid pb-2 ">
            <Container>
            <br />
              <Button color="" 
              className= "btn btn-outline-danger text-light fs-4 " 
              type="button"
              onClick={()=>this.mostrarModalInsertar()}>Nuevo Proyecto</Button>
              <br />
              
              </Container>
          
            
              <ModalBody>
                <FormGroup>    
                  <input
                    className="form-control"
                    readOnly
                    type="text"
                    value={this.state.data.length+1}
                    
                  />
                </FormGroup>
                
                <FormGroup>
                <div className = 'row'>
                    <div className = 'cold-md-12'>
                    <div className="input-group my-2">
                   <div className= "input-group-pretend">
                <span className= 'input-group-text text-danger fs-6 fw-bold'>&#x2328;</span></div>
                  <input
                    type="text"
                    className="form-control fs-6"
                    placeholder= "Nombre proyecto"
                    name="Nombre"
                    onChange={this.handleChange}
                  />
                    </div>
                 </div>
               </div>
                </FormGroup>
              </ModalBody>
    
              <ModalFooter>
                <Button className="d-grid gap-2"
                  color="success"
                  onClick={() => this.insertar()}
                >
                 <h2 className='cold-md-12 text-center fw-bold fs-4 '>Guardar proyecto</h2> 
                </Button>
              </ModalFooter>
            <h3 className ="text-ligth boder boder-button fw-bold">Panel de Proyecto</h3>
              
              <Table>
            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.Nombre}</td>
                </tr>
              ))}
            </tbody>
          </Table>

          </div>
          
    ) }
              
     }
               
  
export default  CrearProyecto
