import React from 'react'
import { Button, FormGroup } from "reactstrap";

export const data = [
    {nombre: 'Elegir Sistema Operativo',estado:true},
    {nombre: 'Elegir Base de Datos',estado:false},
    {nombre: 'Elegir lenguaje',estado:true},
    {nombre: 'Elegir Frameworks',estado:false},
   {nombre: 'Tarea Nueva', estado:false}
]

class FormularioTarea extends React.Component {
 
    state = {
        data: data,
        modalActualizar: false,
        modalInsertar: false,
        form: {
          id: "",
          Nombre: ""
        },
      };
      
      activarFormulario = () => {
        this.setFormulario ({Formulario :false});
      }

      mostrarModalActualizar = (dato) => {
        this.setState({
          form: dato,
          modalActualizar: true,
        });
      };
    
      cerrarModalActualizar = () => {
        this.setState({ modalActualizar: false });
      };
    
      mostrarModalInsertar = () => {
        this.setState({
          modalInsertar: true,
        });
      };
    
     
      insertar= ()=>{
        var valorNuevo= {...this.state.form};
        valorNuevo.id=this.state.data.length+1;
        var lista= this.state.data;
        lista.push(valorNuevo);
        this.setState({ modalInsertar: false, data: lista });
      }
    
      handleChange = (e) => {
        this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };
    
      render() {
       
     



    return (
        // cambiar colores a los datos del formulario
        <div className= "border border-ligth mt-2">
            <h2 className= "text-light fw-bold fs-1 text-center mb-0 mt-2">Formulario de Tareas</h2>
            <from className= "">
                <div className= 'row'>
                <div className = 'cold-md-12'>
                    <div className= "input-group mb-2">
                        <div className= "input-group-prepend">
                        <span className= 'input-group-text text-danger fs-3 fw-bold'>&#x2328;</span> 
                        </div>
                        {/* aqui esta el input de tarea */}
                        <input
                            type="text"
                            className="form-control fs-3"
                            placeholder="Nombre de la tarea"
                            name="nombre"
                        />
                    </div>
                </div>
                </div>
                {/* aqui va el boton */}
                <div className='row '>
                    <div className = 'cold-md-12 text-center '>
                        <div className="d-grid">
                        <Button className="d-grid gap-2"
                         color="success"
                         onClick={insert}
                         >
                             <h2 className='cold-md-12 text-center fw-bold fs-4 '>Guardar proyecto</h2> 
                         </Button>
                        
                    </div>
                    </div>
                </div>
            </from>

        </div>
    )
}
      }

export default FormularioTarea
