import React, { useState } from 'react' 

const CrearProyecto = () => {
    //STATE
    const [proyecto, guardarProyecto] = useState ({nombre:''})
    const [formulario, setFormulario] = useState (false)
    const { nombre } = proyecto 

    const activarFormulario = () => {
        setFormulario(!formulario)
    }
    //Guardar STATE
    const onChange = e =>{
        guardarProyecto({...proyecto,[e.target.name]:e.target.value})
    }
    //Guardar Proyecto
    const onSubmit = e =>{
        e.preventDefault();
        
    //Validar Formulario
        if(nombre.trim() === ''){
            alert('Todos los campos obligatorios');
        }

    }

    return (
    <>
        <div className= "d-grid pb-2">
                <button className= "btn btn-outline-danger text-light fw-bold fs-2"
                    type= "button"
                    onClick= {activarFormulario}
                    >Nuevo Proyecto </button>
        </div>
        { formulario
        ?   ( <form onSubmit = {onSubmit}>
                <div className = 'row'>
                    <div className = 'cold-md-12'>
                        <div className="input-group my-2">
                            <div className= "input-group-pretend">
                                <span className= 'input-group-text text-danger fs-4 fw-bold'>*</span>
                            </div>
                        <input
                            type= "text"
                            className= "form-control fs-4"
                            placeholder= "Nombre proyecto"
                            name ="nombre"
                            value = {nombre}
                            onChange = {onChange}

                        />
                        </div>
                    </div> 
                </div>
                <div className ='row' > 
                    <div  className= 'cold-md-12 text-center mb-4 '> 
                        <div className= "d-grid gap-2">
                            <input type="submit" className ="btn btn-success  fs-4 btn-gradient" ></input>
                        </div>
                    </div>

                </div>
            </form> 
         )
         : null
        }
    </>
    ) 
}

export default  CrearProyecto
