import React, {} from 'react' 
import "../../App.css";
import { Table,Button,Container, ModalBody, FormGroup, ModalFooter, } from "reactstrap";
  
  

    const data = [
        { id: 1, Nombre: "Tienda virtual"},
        { id: 2, Nombre:"Intranet"},
        { id: 3, Nombre:"Diseño de un sitio web"},
        { id: 4, Nombre:"app inventario"},
      ];


class CrearProyecto extends React.Component {
 
    state = {
        data: data,
        modalActualizar: false,
        modalInsertar: false,
        form: {
          id: "",
          Nombre: ""
        },
      };
      
      activarFormulario = () => {
        this.setFormulario ({Formulario :false});
      }

      mostrarModalActualizar = (dato) => {
        this.setState({
          form: dato,
          modalActualizar: true,
        });
      };
    
      cerrarModalActualizar = () => {
        this.setState({ modalActualizar: false });
      };
    
      mostrarModalInsertar = () => {
        this.setState({
          modalInsertar: true,
        });
      };
    
     
      insertar= ()=>{
        var valorNuevo= {...this.state.form};
        valorNuevo.id=this.state.data.length+1;
        var lista= this.state.data;
        lista.push(valorNuevo);
        this.setState({ modalInsertar: false, data: lista });
      }
    
      handleChange = (e) => {
        this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };
    
      render() {
       
      
        return (
      
            <div className="d-grid pb-2 bg-light">
            <Container>
            <br />
              <Button color="success" 
              className= "btn btn-outline-ligth text-danger fw-bold fs-2" 
              type="button"
              onClick={()=>this.mostrarModalInsertar()}>Nuevo </Button>
              <br />
              
              </Container>
          
            
              <ModalBody>
                <FormGroup>    
                  <input
                    className="form-control"
                    readOnly
                    type="text"
                    value={this.state.data.length+1}
                    
                  />
                </FormGroup>
                
                <FormGroup>
                <div className = 'row'>
                    <div className = 'cold-md-12'>
                    <div className="input-group my-2">
                   <div className= "input-group-pretend">
                <span className= 'input-group-text text-danger fs-2 fw-bold'>&#128221;</span></div>
                  <input
                    type="text"
                    className="form-control fs-2"
                    placeholder= "Nombre proyecto"
                    name="Nombre"
                    onChange={this.handleChange}
                  />
                    </div>
                 </div>
               </div>
                </FormGroup>
              </ModalBody>
    
              <ModalFooter>
                <Button
                  color="primary"
                  onClick={() => this.insertar()}
                >
                 <h2 className='cold-md-12 text-center fw-bold fs-2 ' >Gu Proyeardarcto</h2> 
                </Button>
            
              </ModalFooter>
              <Table>
            <thead>
              <tr>
                <h1>Panel de Proyecto</h1>
              </tr>
            </thead>

            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.Nombre}</td>
                </tr>
              ))}
            </tbody>
          </Table>

          </div>
          
    ) }
              
     }
               
            

export default  CrearProyecto
