import React from 'react'
import MostrarTareas, {}from './MostrarTareas';
import { Table,Button,Container, ModalBody, FormGroup, ModalFooter, } from "reactstrap";



const data =[
    { id: 1, Nombre: 'Elegir Sistema Operativo'},
    { id: 2, Nombre:'Elegir Base de Datos'},
    { id: 3, Nombre:'Elegir lenguaje'},
    { id: 4, Nombre:'Elegir Frameworks'},
    { id: 5, Nombre: 'Tarea Nueva'},
]


    
class FormularioTarea extends React.Component {
    
    
    state = {
        data: data,
        modalActualizar: false,
        modalInsertar: false,
        form: {   
          id: "",
          Nombre: ""
        },
      };
      
      mostrarModalActualizar = (dato) => {
        this.setState({
          form: dato,
          modalActualizar: true,
        });
      };
    
      cerrarModalActualizar = () => {
        this.setState({ modalActualizar: false });
      };
    
      mostrarModalInsertar = (Tarea) => {
        this.setState({
          modalInsertar: true,
          
        });
      };
    
     
      insertar= ()=>{
        var valorNuevo= {...this.state.form};
        valorNuevo.id=this.state.data.length+1;
        var lista= this.state.data;
        lista.push(valorNuevo);
        this.setState({ modalInsertar: false, data: lista });
      }
    
      handleChange = (e) => {
        this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };



    render() {
    return (
        
        <div>
            
              
        <div className= "border border-light mt-2">
        <h2 className= "text-dark fw-bold fs-1 text-center mb-0 mt-2" >Formulario de Tareas</h2>
        <from className= "">
            <div className= 'row'>
            <div className = 'cold-md-12'>
                <div className= "input-group mb-2">
                    <div className= "input-group-prepend">
                        <span className= "input-group-text text-danger fs-2 fw-bold" >&#128194;</span> 
                    </div>
                    <input
                        type="text"
                        className="form-control fs-2"
                        placeholder="Nombre de la tarea"
                        name="nombre"
                        onChange={this.handleChange}
                       
                    />
                </div>
            </div>
            </div>
            
            <div className='row'>
                <div className = 'cold-md-12 text-center mb-4'>
                    <div className="d-grid">
                     <h2  className="btn btn-dark bg-gradient fs-2 " onClick={() => this.insertar()}  >Guardar Tarea</h2>
                    </div>
                </div>
            </div>
          
        </from>

    </div>
    <Table>
        <thead>
          <tr>
          <h2 className="text-center text-danger bg-light  fw-bold p-4">Proyecto:<span className="text-dark">Comercio Electronico</span></h2>
          </tr>
        </thead>

        <tbody>
          {this.state.data.map((dato) => (
            <tr key={dato.id}>
              <td>{dato.Nombre}</td>
            </tr>
          ))}
        </tbody>
      </Table>   

    </div>

    )
}
}

export default FormularioTarea
